import React from 'react';
import Button from './Button';
import './TodoItem.css';
export type Todo = {
  id: number;
  name: string;
  description: string;
  checked: boolean;
};
interface TodoItemProps {
  todo: Todo;
}

const TodoItem: React.FC<TodoItemProps> = ({ todo }) => {
  //   return <div>{todo.name}</div>;
  return (
    <div className='item'>
      <div>
        <div aria-hidden className='item__title'>
          {todo.name}
        </div>
        <div aria-hidden className='item__description'>
          {todo.description}
        </div>
        <div className='item__button'>
        {/* <Button color='orange'>
                Edit
            <Button/> */}
            <Button color="red">
                {Delete} 
            </Button>
        </div>
      </div>
    </div>
  );
};

export default TodoItem;
