import React from 'react';
import { TodoComment } from 'typescript';
import './App.css';
import Header from './components/Header';
import TodoList from './components/TodoList';
import TodoPanel from './components/TodoPanel';

export type Todo = {
  id: number;
  name: string;
  description: string;
  checked: boolean;
};



function App() {
  const [todos, setTodos] = React.useState(DEFAULT_TODO_LIST);
  const [todoIdEdit, setTodoIdEdit] = React.useState<Todo['id'] | null>(null);

  const selectTodoForEdit = (id: Todo['id']) => {
    setTodoIdEdit(id);
  };
  const addTodo = ({ name, description }: Omit<Todo, 'checked' | 'id'>) => {
    setTodos([
      ...todos,
      { id: todos[todos.length - 1].id + 1, description, name, checked: false },
    ]);
  };
  const checkTodo = (id: Todo['id']) => {
    setTodos(
      todos.map((todo) => {
        if (todo.id === id) {
          return { ...todo, checked: !todo.checked };
        }
        return todo;
      })
    );
  };
  const deleteTodo = (id: Todo['id']) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };
  const changeTodo = ({ name, description }: Omit<Todo, 'checked' | 'id'>) => {
    setTodos(
      todos.map((todo) => {
        if (todo.id === todoIdEdit) {
          return { ...todo, name, description };
        }
        return todo;
      })
    );
    setTodoIdEdit(null);
  };
  return (
    <div className='main'>
      <div className='todo'>
        <Header todoCount={todos.length} />
        <TodoPanel mode='add' addTodo={addTodo} />
        <TodoList
          todoIdEdit={todoIdEdit}
          todos={todos}
          changeTodo={changeTodo}
          checkTodo={checkTodo}
          deleteTodo={deleteTodo}
          selectTodoForEdit={selectTodoForEdit}
        />
      </div>
    </div>
  );
}

export default App;
